INSERT INTO `barber_booking_emails` (`email_id`, `email_name`, `email_subject`, `email_text`, `email_cancel_text`, `email_signature`) VALUES
(1, 'Email sent automatically to customer to confirm reservation', 'Confirmation of Booking', '<p>Hello [customer-name],</p><p>thanks for Booking,<br />your Reservation details:</p><p>[reservation-details]</p><p></p>', '<p>If you want to cancel your reservation, simply click the link below:<br />[cancellation-link]<br />If the link is not clickable, please copy and paste this URL into your browser\'s address bar: [cancellation-link-url]</p>', '<p>Thanks,<br />The Team<br /><br /></p>'),
(2, 'Email sent automatically to customer to make him/her confirm the reservation via link', 'Confirm your Booking', '<p>Hello [customer-name],</p><p>we received your reservation request:</p><p>[reservation-details]</p><p>To confirm your reservation, simply click the link below to verify your email address:</p><p>[confirmation-link]</p><p>If the link is not clickable, please copy and paste this URL into your browser\'s address bar: [confirmation-link-url]</p>', '', '<p>Thanks,<br />The Team<br /><br /></p>'),
(3, 'Email sent automatically to customer to tell him/her that you have to confirm his/her reservation', 'Confirmation of Booking', '<p>Hello [customer-name],</p><p>thanks for Booking, you\'ll receive a mail when your reservation/s will be confirmed.<br />Your Reservation details:</p><p>[reservation-details]</p>', '', '<p>Thanks,<br />The Team<br /><br /></p>'),
(4, 'Email sent to customer when reservation is confirmed manually from admin panel', 'Booking Confirmed', '<p>Hello [customer-name],</p><p>Your reservation has been confirmed.<br />Reservation details:</p><p>[reservation-details]</p>', '<p>If you want to cancel your reservation, simply click the link below:<br />[cancellation-link]<br />If the link is not clickable, please copy and paste this URL into your browser\'s address bar: [cancellation-link-url]</p>', '<p>Thanks,<br />The Team<br /><br /></p>');
